import {stringifyQuery} from "ufo";
export const getImage = (src, {modifiers, baseURL = "/_vercel/image"} = {}) => {
  return {
    url: baseURL + "?" + stringifyQuery({
      url: src,
      w: modifiers?.width ? String(modifiers.width) : void 0,
      q: String(modifiers?.quality || "100")
    })
  };
};
